import React, { PureComponent } from "react";
import { connect } from "react-redux";
import {
  BarChart,
  Bar,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis,
  CartesianGrid,
} from "recharts";
import { withTranslation } from "react-i18next";
import PropTypes from "prop-types";
import Panel from "../../../../shared/components/Panel";

import getTooltipStyles from "../../../../shared/helpers";
import axios from "axios";
import { range } from "lodash";

class SalesStatistisBar extends PureComponent {
  static propTypes = {
    t: PropTypes.func.isRequired,
    themeName: PropTypes.string.isRequired,
  };

  constructor(props) {
    super(props);
    this.state = {
      yearly: true,
      dataMonthly: [],
      dataWeekly: [],
      dataDaily: [],
      query_date: this.props.query_date,
      date_change: true
    };
    this.getDailyData();
    this.getWeaklyData();
    this.getMonthlyData();
  }

  handleChange = () => {
    this.setState((prevState) => ({ yearly: !prevState.yearly }));
    console.log(this.props.query_date)
  };
  getDailyData = () => {
    var user_id=localStorage.getItem('user_id')
    var date=this.props.query_date
    var path="/userEnergyPerHour/"+user_id+"?date="+date
    axios.get(path).then((res) => {
      var data = [];
      for (var i = 0; i < res.data.length; i++) {
        data.push({ name: String(res.data[i].time), uv: res.data[i].power });
      }
      this.setState({ dataDaily: data });
    });
  };
  getWeaklyData = () => {
    var user_id=localStorage.getItem('user_id')
    var date=this.props.query_date
    var path="/userEnergyPerWeek/"+user_id+"?date="+date
    axios.get(path).then((res) => {
      var data = [];
      for (var i = 0; i < res.data.length; i++) {
        data.push({
          name: res.data[i].date.substring(5, 10),
          uv: res.data[i].power,
        });
      }
      this.setState({ dataWeekly: data });
    });
  };
  getMonthlyData = () => {
    var user_id=localStorage.getItem('user_id')
    var date=this.props.query_date
    var path="/userEnergyPerMonth/"+user_id+"?date="+date
    axios.get(path).then((res) => {
      var data = [];
      for (var i = 0; i < res.data.length; i++) {
        data.push({
          name: res.data[i].date.substring(8, 10),
          uv: res.data[i].power,
        });
      }
      this.setState({ dataMonthly: data });
    });
  };
  getData(){
    if(this.props.date_change){
      this.getDailyData();
      this.getWeaklyData();
      this.getMonthlyData();
    }
   
  }
  render() {
    const { t, themeName } = this.props;
    const { yearly } = this.state;
    const dataYearly = [
      { name: "Mon", uv: 4000 },
      { name: "Tus", uv: 3000 },
      { name: "Wed", uv: 2000 },
      { name: "Tuh", uv: 2780 },
      { name: "Fri", uv: 1890 },
      { name: "Sat", uv: 2390 },
      { name: "Sun", uv: 3490 },
    ];

    const dataMontly = [
      { name: "01:00", uv: 3654 },
      { name: "02:00", uv: 2578 },
      { name: "03:00", uv: 2000 },
      { name: "04:00", uv: 4383 },
      { name: "05:00", uv: 1890 },
      { name: "06:00", uv: 2390 },
      { name: "07:00", uv: 3490 },
      { name: "08:00", uv: 2000 },
      { name: "09:00", uv: 2780 },
      { name: "10:00", uv: 1890 },
      { name: "11:00", uv: 3578 },
      { name: "12:00", uv: 3644 },
      { name: "13:00", uv: 4000 },
      { name: "14:00", uv: 3000 },
      { name: "15:00", uv: 2000 },
      { name: "16:00", uv: 2780 },
      { name: "17:00", uv: 1890 },
      { name: "18:00", uv: 2390 },
      { name: "19:00", uv: 3490 },
      { name: "20:00", uv: 2000 },
      { name: "21:00", uv: 2780 },
      { name: "22:00", uv: 1890 },
      { name: "23:00", uv: 3578 },
      { name: "24:00", uv: 2390 },
    ];

    return (
      <Panel
        md={12}
        lg={12}
        title={t("Energy Consumption")}
        subhead="See how your energy used"
      >
      <div>
        <p>Total Daily Energy Consumption</p>
        <p>6000 kwh/day</p>
      </div>
     
      {
        this.getData()
      }
        <div dir="ltr">
          <ResponsiveContainer height={260} className="dashboard__area">
            {yearly ? (
              <BarChart
                data={this.state.dataDaily}
                margin={{ top: 20, left: -15 }}
              >
                <XAxis dataKey="name" tickLine={false} label="HOUR" />
                <YAxis tickLine={false} label="kwh"/>
                <Tooltip {...getTooltipStyles(themeName, "defaultItems")} />
                <CartesianGrid vertical={false} />
                <Bar dataKey="uv" name="KWh" fill="#4ce1b6" barSize={40} />
              </BarChart>
            ) : (
              <BarChart
                data={this.state.dataWeekly}
                margin={{ top: 20, left: -15 }}
              >
                <XAxis dataKey="name" tickLine={false} label="HOUR" />
                <YAxis tickLine={false} label="kwh"/>
                <Tooltip {...getTooltipStyles(themeName, "defaultItems")} />
                <CartesianGrid vertical={false} />
                <Bar dataKey="uv" name="kwh" fill="#ff4861" barSize={140} />
              </BarChart>
            )}
          </ResponsiveContainer>
        </div>
        <hr />
        <label htmlFor="sales" className="toggle-btn dashboard__sales-toggle">
          <input
            className="toggle-btn__input"
            type="checkbox"
            name="sales"
            id="sales"
            onChange={this.handleChange}
          />
          <span className="dashboard__sales-toggle-left">Hourly</span>
          <span className="toggle-btn__input-label" />
          <span className="dashboard__sales-toggle-right">Daily</span>
        </label>
        <hr />
        <p>Your Energy Consumption Monthly</p>
        <hr />
        <div dir="ltr">
          <ResponsiveContainer height={260} className="dashboard__area">
            {
              <BarChart
                data={this.state.dataMonthly}
                margin={{ top: 20, left: -15 }}
              >
                <XAxis dataKey="name" tickLine={false} />
                <YAxis tickLine={false} />
                <Tooltip {...getTooltipStyles(themeName, "defaultItems")} />
                <CartesianGrid vertical={false} />
                <Bar dataKey="uv" name="Sales" fill="#4ce1b6" barSize={40} />
              </BarChart>
            }
          </ResponsiveContainer>
        </div>
        <hr />
      </Panel>
    );
  }
}

export default connect((state) => ({ themeName: state.theme.className }))(
  withTranslation("common")(SalesStatistisBar)
);
